import { FormBuilder, Validators } from '@angular/forms';
import { ActivatedRoute, Router } from '@angular/router';
import { AuthService } from '../Shared/auth.service';
let LoginComponent = class LoginComponent {
    constructor(fb, http, route, router, authService) {
        this.fb = fb;
        this.http = http;
        this.route = route;
        this.router = router;
        this.authService = authService;
        this.model = {};
    }
    ngOnInit() {
        // this.loginForm=this.formbBuilder.group({
        //   username : [''],
        //   password : ['']
        // });
        this.CreateLoginForm();
    }
    // login(){
    //   this.http.get<any>('http://www.classreminder-1a.somee.com/api/login/allUser')
    //   .subscribe(res=>{
    //     const user = res.find((a:any)=>{
    //       return a.username === this.loginForm.value.username && a.password === this.loginForm.value.password;
    //     });
    //     if(user){
    //       alert("Login Success");
    //       this.loginForm.reset();
    //       this.router.navigate(['/list']);
    //     }else{
    //       alert("Login Failed");
    //     }
    //   }, err=>{
    //     alert("Login Failed");
    //   });
    // }
    CreateLoginForm() {
        this.loginForm = this.fb.group({
            email: ['', [Validators.required, Validators.email]],
            password: ['', [Validators.required]],
            rememberMe: [true]
        });
    }
    login() {
        if (this.loginForm.valid) {
            this.model = Object.assign({}, this.loginForm.value);
            this.authService.login(this.model).subscribe(data => {
                alert("login success");
                this.router.navigate(['/list']);
            }, (error) => {
                console.log(error);
            }, () => {
                // window.location.href = localStorage.getItem('index');
                // window.location.href = '2fa';
            });
        }
    }
};
