import { map } from 'rxjs/operators';
import { environment } from 'src/environments/environment';
export class AuthService {
  constructor(http) {
    this.http = http;
    this.classUrl = environment.baseUrl + 'api/ClassList/';
    this.apiUrl = environment.apiUrl;
  }

  postClassList(data) {
    return this.http.post(this.classUrl + 'saveClassList', data);
  }

  getClassById(id) {
    return this.http.get(this.classUrl + 'getClassById?Id=' + id);
  }

  deleteClassList(id) {
    return this.http.get(this.classUrl + 'deleteClassList?Id=' + id);
  }

  getAllClassList() {
    return this.http.get(this.classUrl + 'getAllClassList');
  }

  login(model) {
    return this.http.post(this.apiUrl + 'Account/Login', model).pipe(map(response => {
      console.log(response);
      const user = response;
      localStorage.setItem('token', user.email);
    }));
  }

  isLoggedIn() {
    const token = localStorage.getItem('token');
    return !!token;
  }

  register(model) {
    return this.http.post(this.apiUrl + 'Account/Register', model);
  }

}
